(function(){'use strict';angular.module('app').controller('HomeController',HomeController);HomeController.$inject=['UserService','$rootScope'];function HomeController(UserService,$rootScope){var home=this;home.user=null;initController();function initController(){loadCurrentUser()}
function loadCurrentUser(){UserService.GetByUsername($rootScope.globals.currentUser.username).then(function(user){home.user=JSON.parse(JSON.stringify(user)).data})}
$(function(){var links=$('.sidebar-links > div');links.on('click',function(){links.removeClass('selected');$(this).addClass('selected')})})}})()
