(function(){'use strict';angular.module('app').controller('LoginController',LoginController);LoginController.$inject=['$location','AuthenticationService','FlashService'];function LoginController($location,AuthenticationService,FlashService){var vm=this;vm.login=login;(function initController(){AuthenticationService.ClearCredentials()})();function login(){vm.dataLoading=!0;AuthenticationService.Login(vm.username,vm.password,function(response){if(response.success){AuthenticationService.SetCredentials(vm.username,vm.password);$location.path('/home')}else{FlashService.Error(response.message);vm.dataLoading=!1}})};$(function(){var links=$('.sidebar-links > div');links.removeClass('selected');links.toggleClass('link-unavailable');links.on('click',function(){})})}})()
