var express=require('express');var app=express();var router=express.Router();var mongoose=require('mongoose');var ejs=require('ejs');var fs=require('fs');var fechaNew=require('fecha');var Registro=require('../models/Registro.js');var User=require('../models/User.js');var Campo=require('../models/Campos.js');app.use(express.static('/mnt/Development/appMCR/api-mcr/routes/templates'));const sendEmail=require('./transport.min.js');var templateString=fs.readFileSync('/mnt/Development/appMCR/api-mcr/routes/templates/correo.ejs','utf-8');router.get('/sendMail/:correo/:listado/:remitente',function(req,res,next){var listaEventos=[];console.log('listadoassa: '+req.params.listado);var Sequence=exports.Sequence||require('sequence').Sequence,sequence=Sequence.create(),err;var array=req.params.listado.split(',');sequence.then(function(next){setTimeout(function(){for(var i=0,len=array.length;i<len;i++){Registro.findById(array[i],function(err,post){console.log('********************cdscenviandoCorreo: '+JSON.stringify(post)+post.responsable);listaEventos.push(post)})}
next(err,listaEventos)},500)}).then(function(next,err,listaEventos){setTimeout(function(){console.log('************Obj: '+JSON.stringify(listaEventos[0]));fecha=new Date().toISOString().replace(/T/,' ').replace(/\..+/,'').toLocaleString("en-US",{timeZone:"America/Santiago"});fechaNew.i18n={dayNamesShort:['Dom','Lun','Mar','Mie','Jue','Vie','Sab'],dayNames:['Domingo','Lunes','Martes','Miércoles','Jueves','Viernes','Sábado'],monthNamesShort:['Ene','Feb','Mar','Abr','May','Jun','Jul','Ago','Sept','Oct','Nov','Dic'],monthNames:['Enero','Febrero','Marzo','Abril','Mayo','Junio','Julio','Agosto','Septiembre','Octubre','Noviembre','Diciembre'],amPm:['am','pm'],DoFn:function(D){return D+['th','st','nd','rd'][D%10>3?0:(D-D%10!==10)*D%10]}};fechaNew.masks={'default':'ddd MMM DD YYYY HH:mm:ss',shortDate:'M/D/YY',mediumDate:'MMM D, YYYY',longDate:'MMMM D, YYYY',fullDate:'dddd, MMMM D, YYYY',shortTime:'HH:mm',mediumTime:'HH:mm:ss',longTime:'HH:mm:ss.SSS'};fechaNew.masks.myMask='HH:mm:ss YY/MM/DD';fechaNew.format(new Date(),'myMask');fecha2=fechaNew.format(new Date(),'dddd Do MMMM HH:mm, YYYY -0300');console.log('timezone; '+fecha2);message=ejs.render(templateString,{result:listaEventos,fecha:fecha2,remitente:req.params.remitente});sendEmail(req.params.correo,'Notificacion eventos al aire MCR',message);next()},500)});res.json({envio:'ok'})});router.get('/queryTimesbyparameters/:key/:value/:fechaIni/:fechaFin',function(req,res,next){console.log('********************key: '+req.params.key);console.log('********************value: '+req.params.value);key=req.params.key;value=req.params.value;Registro.count({[key]:value,fechaevento:{$gte:req.params.fechaIni,$lt:req.params.fechaFin}},function(err,registro){if(err)return next(err);res.json({value:registro})})});router.get('/',function(req,res,next){Registro.find(function(err,registro){if(err)return next(err);res.json(registro)})});router.get('/Users',function(req,res,next){User.find(function(err,users){if(err)return next(err);res.json(users)})});router.get('/Users/:id',function(req,res,next){User.findById(req.params.id,function(err,post){if(err)return next(err);res.json(post)})});router.get('/Users/username/:username',function(req,res,next){User.findOne({username:req.params.username},function(err,post){if(err)return next(err);res.json(post)})});router.post('/Users/username/:username',function(req,res,next){User.remove({username:req.params.username},function(err,post){if(err)return next(err);res.json(post)})});router.post('/Users',function(req,res,next){console.log('post');User.create(req.body,function(err,post){if(err)return next(err);res.json(post)})});router.get('/Campos/:key',function(req,res,next){Campo.find({key:req.params.key},function(err,campos){if(err)return next(err);res.json(campos)})});router.post('/Campos/update',function(req,res,next){console.log('post');Campo.insertMany(req.body,function(err,post){if(err)return next(err);res.json(post)})});router.post('/Campos',function(req,res,next){console.log('post');Campo.create(req.body,function(err,post){if(err)return next(err);res.json(post)})});router.post('/Campos/remove/:key',function(req,res,next){Campo.remove({key:req.params.key},function(err,post){if(err)return next(err);res.json(post)})});router.post('/',function(req,res,next){console.log('post');Registro.create(req.body,function(err,post){if(err)return next(err);res.json(post)})});router.get('/:id',function(req,res,next){Registro.findById(req.params.id,function(err,post){if(err)return next(err);res.json(post)})});router.put('/:id',function(req,res,next){Registro.findByIdAndUpdate(req.params.id,req.body,function(err,post){if(err)return next(err);res.json(post)})});router.put('/informado/:id',function(req,res,next){Registro.findByIdAndUpdate(req.params.id,{$set:{informado:!0}},function(err,post){if(err)return next(err);res.json(post)})});router.delete('/:id',function(req,res,next){Registro.findByIdAndRemove(req.params.id,req.body,function(err,post){if(err)return next(err);res.json(post)})});router.get('*',function(req,res){res.sendfile('./public/index.html')});module.exports=router
